import React, { Component } from 'react';
import desk_img from '../img/Desk_Items.png';
import desk from '../img/Desk.png';
import computer_img from '../img/Computer.png';
import computer_close_img from '../img/Computer_Close.png';
import mouse_img from '../img/Mouse.png';

class Desk extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showComputer: false,
    }
    this.clickHandler = this.clickHandler.bind(this);
  }
  clickHandler(target) {
    if (target == "computer") {
      this.setState({
        showComputer: true,
      }, () => {
        {renderOpaque()}
        console.log(this.state.show);
        console.log(target)
      });
    }
  }


  render() {
    return (
        <div class="region-desk">
            {/* <img src={desk_img} alt="Desk" class="desk"></img> */}
            <img src={desk} alt="Desk" class="desk"></img>
            <img src={computer_img} alt="Computer" class="computer" onClick={() => this.clickHandler("computer")}></img>
            {this.state.showComputer ? renderComputer() : null}
            {this.state.showComputer ? <img src={mouse_img} alt="Mouse" class="mouse"></img> : null}
        </div>
    );
  }
}

const renderComputer = () => {
  return (
    <div class="computer-close">
      <img src={computer_close_img} alt="Computer" class="computer"></img>
    </div>
  );
}


export default Desk;
