/* Source: https://codemyui.com/switch-between-mason-grid-and-slider-view/ */
import React, { Component } from 'react';

import '../Assets/css/gridstyle.css'
import singify_img from './singify.png'

class Grid extends Component {
  constructor(props) {
    super(props);
    this.state = {
      grid: true,
    }
  }
  handleClick(target) {
    switch(target) {
      case "grid":
        this.setState({
          grid: true,
        })
        break;
      case "carousel":
        this.setState({
          grid: false,
        })
    }
  }

  render() {
    const Project = (name, img) => {
      return (
        <p>

          {/* <img src={img}></img> */}
          <span></span>
          <span className="short"></span>

        </p>
      );
    }

    const renderGallery = () => {
      return (
        <div className="thumbs">  
              {Project("Singify", singify_img)}
              {Project("Singify", singify_img)}
              {Project("Singify", singify_img)}
              {Project("Singify", singify_img)}
              {Project("Singify", singify_img)}
          </div>
      );
    }

    const renderCarousel = () => {
      return (
        <div id="carousel">
              <div className="innerCarousel">
                  <div>

                  </div>
                  <div>
                      <p className="current">
                          <span></span>
                          <span className="short"></span>
                      </p>
                  </div>
                  <div>    
                      <p>
                          <span></span>
                          <span className="short"></span>
                      </p>
                  </div>
                  <div>    
                      <p>
                          <span></span>
                          <span className="short"></span>
                      </p>
                  </div>
                  <div>
                      <p>
                          <span></span>
                          <span className="short"></span>
                      </p>
                  </div>
                  <div>    
                      <p>
                          <span></span>
                          <span className="short"></span>
                      </p>
                  </div>
                  <div>    
                      <p>
                          <span></span>
                          <span className="short"></span>
                      </p>
                  </div>
              </div>
          </div>
      );
    }


    const renderGrid = () => {
      return (
        <div id="grid">
          <div id="grid-selector">
              <div id="viewCarousel" onClick={() => this.handleClick("carousel")} className={!this.state.grid ? "active" : null}></div>
              <div id="viewGrid" onClick={() => this.handleClick("grid")} className={this.state.grid ? "active" : null}>
                <p></p>
                <p></p>
                <p></p>
                <p></p>            
              </div>        
          </div>

          <div id="headline">
              <p></p>
              <p className="short"></p>
          </div>

          {this.state.grid ? renderGallery() : renderCarousel()}

          <script src="https://ajax.googleapis.com/ajax/libs/jquery/1.11.2/jquery.min.js"></script>
        </div>
      );
    }
    return (
        renderGrid()
    );
  }
}

export default Grid;
