/* Source: https://codemyui.com/switch-between-mason-grid-and-slider-view/ */
import React, { Component } from 'react';

import '../Assets/css/gridstyle.css'

class Grid extends Component {
  constructor(props) {
    super(props);
    this.state = {
      grid: true,
    }
  }
  // componentDidMount() {
  //   const script = document.createElement('script');
  //   script.src = "./gridwork.js"
  //   script.async = true;
  //   document.body.appendChild(script);
  //   alert("appended")
  // };
  handleClick(target) {
    // alert("clicked")
    switch(target) {
      case "grid":
        this.setState({
          grid: true,
        });
        break;
      case "carousel":
        this.setState({
          grid: false,
        });
        this.setCarousel();
    }
  }

  setCarousel(){

    // building the width of the casousel
    var carouselWidth = 0;
    $('#carousel div').each(function(){
      carouselWidth += carouselSlideWidth;
    });		

    $(carousel)[0].style = "";
    $(carousel).css('width', carouselWidth);
    // Load Next Image
    $(carouselSlides).eq(currentSlide).prev().find('p').unbind( "click" ).click(function(){

      if($(this).hasClass('current')){return;}
      var currentLeft = Math.abs(parseInt($(carousel).css("left")));
      var newLeft = currentLeft - carouselSlideWidth;		


      if(isAnimating === true){return;}
      $(carousel).css({'left': "-" + newLeft + "px",
                   "transition": "500ms cubic-bezier(0,.93,.33,.99)"
                 });
      isAnimating = true;
      $(this).addClass("current");
      $(carouselSlides).eq(currentSlide).find('p')[0].className ="";
      setTimeout(function(){
        isAnimating = false;
        currentSlide--;
        setCarousel();
      }, 500);			

    });		

    $(carouselSlides).eq(currentSlide).next().find('p').unbind( "click" ).click(function(){		

      if($(this).hasClass('current')){return;}	
      var currentLeft = Math.abs(parseInt($(carousel).css("left")));
      var newLeft = currentLeft + carouselSlideWidth;			

      if(isAnimating === true){return;}	
      $(this).addClass("current");
      $(carouselSlides).eq(currentSlide).find('p')[0].className ="";
      $(carousel).css({'left': "-" + newLeft + "px",
                   "transition": "500ms cubic-bezier(0,.93,.33,.99)"
                 });
      isAnimating = true;
      setTimeout(function(){
        isAnimating = false;
        currentSlide++;
        setCarousel();
      }, 500);			
    });
  }

  render() {
    // useEffect(() => {
    //   const script = document.createElement('script');
    //   script.src = "./gridwork.js"
    //   script.async = true;
    //   document.body.appendChild(script);

    //   return () => {
    //     document.body.removeChild(script);
    //   }
    // });



    const renderGrid = () => {
      return (
        <div id="grid">
          <div id="grid-selector">
              <div id="viewCarousel" onClick={() => this.handleClick("carousel")} className={!this.state.grid ? "active" : null}></div>
              <div id="viewGrid" onClick={() => this.handleClick("grid")} className={this.state.grid ? "active" : null}>
                <p></p>
                <p></p>
                <p></p>
                <p></p>            
              </div>        
          </div>

          <div id="headline">
              <p></p>
              <p className="short"></p>
          </div>

          <div className="thumbs">  
              <p>
                  <span></span>
                  <span className="short"></span>
              </p>
              <p>
                  <span></span>
                  <span className="short"></span>
              </p>
              <p>
                  <span></span>
                  <span className="short"></span>
              </p>
              <p>
                  <span></span>
                  <span className="short"></span>
              </p>
              <p>
                  <span></span>
                  <span className="short"></span>
              </p>
              <p>
                  <span></span>
                  <span className="short"></span>
              </p>
          </div>

          <div id="carousel">
              <div className="innerCarousel">
                  <div>

                  </div>
                  <div>
                      <p className="current">
                          <span></span>
                          <span className="short"></span>
                      </p>
                  </div>
                  <div>    
                      <p>
                          <span></span>
                          <span className="short"></span>
                      </p>
                  </div>
                  <div>    
                      <p>
                          <span></span>
                          <span className="short"></span>
                      </p>
                  </div>
                  <div>
                      <p>
                          <span></span>
                          <span className="short"></span>
                      </p>
                  </div>
                  <div>    
                      <p>
                          <span></span>
                          <span className="short"></span>
                      </p>
                  </div>
                  <div>    
                      <p>
                          <span></span>
                          <span className="short"></span>
                      </p>
                  </div>
              </div>
          </div>
          <script src="https://ajax.googleapis.com/ajax/libs/jquery/1.11.2/jquery.min.js"></script>
        </div>
      );
    }
    return (
        renderGrid()
    );
  }
}

export default Grid;
