import React, { Component } from 'react';
import desk_img from '../img/Desk_Items.png';
import desk from '../img/Desk.png';
import computer_img from '../img/Computer.png';
import mouse_img from '../img/Mouse.png';

class Desk extends Component {
  constructor(props) {
    super(props);
    // console.log("props:");
    // console.log(this.props.onClickCallback);
    // this.handleClick = this.ha
    this.state = {
      showComputer: false,
    }
    this.clickHandler = this.clickHandler.bind(this);
  }
  clickHandler(target) {
    if (target == "computer") {
      this.setState({
        showComputer: true,
      }, () => {
        // renderOpaque();
        console.log(this.state.showComputer);
        console.log(target)
      });
    }
  }


  render() {
    return (
        <div className="region-desk">
            {/* <img src={desk_img} alt="Desk" class="desk"></img> */}
            <img src={desk} alt="Desk" className="desk"></img>
            {/* <img src={computer_img} alt="Computer" class="computer" onClick={() => this.clickHandler("computer")}></img> */}
            <img src={computer_img} alt="Computer" className="computer" onClick={this.props.clicker()}></img>
            {this.state.showComputer ? <img src={mouse_img} alt="Mouse" className="mouse"></img> : null}
        </div>
    );
  }
}




export default Desk;
