import React, { Component } from 'react';
import desk_img from '../img/Desk_Items.png';
import desk from '../img/Desk.png';
import computer_img from '../img/Computer.png';
import mouse_img from '../img/Mouse.png';

class Desk extends Component {
  state = {
    show: false,
  }
  constructor(props) {
    super(props);
    // this.state = {
    //   show: false,
    // }
    this.clickHandler = this.clickHandler.bind(this);
  }
  clickHandler(){ // declare a function that updates the state
    // elementIsClicked = true;
    // alert("clicked");
    this.setState({
      show: true,
    });
    // alert("this " + this.state.show);
    console.log("here");
    console.log(this.state.show);
    alert("clicked");
  }
  render() {
    // var elementIsClicked = false; // declare the variable that tracks the state


    // var element = document.getElementById('computer'); // grab a reference to your element
    // element.addEventListener('click', clickHandler); // associate the function above with the click event


    return (
        <div class="region-desk">
            {/* <img src={desk_img} alt="Desk" class="desk"></img> */}
            <img src={desk} alt="Desk" class="desk"></img>
            <img src={computer_img} alt="Computer" class="computer" onClick={this.clickHandler}></img>
            {this.state.show ? <img src={mouse_img} alt="Mouse" class="mouse"></img> : null}
        </div>
    );
  }
}

export default Desk;
