    //   }, () => {
    //     // renderOpaque();
    //     console.log(this.state.show);
    //     console.log(target)

    //   });
    // }
  }


  render() {
    return (
      <div className="App">
        <Background></Background>
        {/* <Desk clickHandler={this.handle}></Desk> */}
        <Desk clickHandler={this.handle()}></Desk>
        <Shelf></Shelf>
        <Window></Window>
        <Banner></Banner>
        {this.state.showComputer ? renderOpaque() : null}
        {this.state.showComputer ? renderComputer() : null}
      </div>
    );
  }
}

const renderOpaque = () => {
  return (
    <div class="opaque">
      hello world
      {console.log("showing")}
    </div>
  );
}

const renderComputer = () => {
  return (
    <div class="computer-close">
      <img src={computer_close_img} alt="Computer" class="computer"></img>
    </div>
  );
}

export default App;
