import React, { Component } from 'react';

// Components
import Background from './components/Background';
import Desk from './components/Desk';
import Shelf from './components/Shelf';
import Window from './components/Window';
import Banner from './components/Banner';
import computer_close_img from './img/Computer_Close.png';


// Includes
import './Assets/css/default.min.css'

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showComputer: false,
    }
    this.clickEvent = this.clickEvent.bind(this);
  }

  clickEvent(target) {
    console.log(target);
    if (target == "computer") {
      this.setState({
        showComputer: true,
      }, () => {
        // renderOpaque();
        console.log(this.state.show);
        console.log(target)

      });
    }
  }


  render() {
    return (
      <div className="App">
        <Background></Background>
        <Desk clickHandler={() => this.clickEvent("computer")}></Desk>
        <Shelf></Shelf>
        <Window></Window>
        <Banner></Banner>
        {this.state.showComputer ? renderOpaque() : null}
        {this.state.showComputer ? renderComputer() : null}
      </div>
    );
  }
}

const renderOpaque = (state) => {
  return (
    <div className="opaque" onClick={this.state.showComputer == false}>
      hello world
      {console.log("showing")}
    </div>
  );
}

const renderComputer = () => {
  return (
    <div class="computer-close">
      <img src={computer_close_img} alt="Computer" class="computer-close"></img>
    </div>
  );
}

export default App;
